


#include  using namespace std; int computeDifference(int startHours, int futureHours, bool startAM, int startMinutes, int futurfutureMinutes, bool futureAM); void main() { int startHr, futureHr, startMin, futureMin; int diffMIn; char choice; char a_char; char m_char; bool startAM; bool futureAM; do { cout << "Enter start time details in 12 hour format." << endl; cout << "Enter hours:"; cin >> startHr; cout << "Enter minutes:"; cin >> startMin; cout << "Enter AM (or) PM:"; cin >> a_char >> m_char; if (a_char == 'A' || a_char == 'a') startAM = true; else startAM = false; cout << "Enter future time details in 12 hour format." << endl; cout << "Enter hours:"; cin >> futureHr; cout << "Enter minutes:"; cin >> futureMin; cout << "Enter Am (or) PM:"; cin >> a_char >> m_char; if (a_char == 'A' || a_char == 'a') futureAM = true; else futureAM = false; int diffMin = computeDifference(startHr, futureHr, startAM, startMin, futureMin, futureAM); cout << "Difference between two timings is " << diffMin << "minutes." << endl; cout << " Press 'y' to repeat calculations:"; cin >> choice; } while (choice == 'y' || choice == 'Y'); cout << " "; system("pause"); } int computeDifference(int startHours, int futureHours, bool startAM, int startMinutes, int futurfutureMinutes, bool futureAM) { int startTime, futureTime; int diffMinutes = 0; startTime = startHours * 60 + startMinutes; futureTime = futureHours * 60 +futurfutureMinutes; if (startAM == futureAM) { if (startTime) diffMinutes = futureTime - startTime; else diffMinutes = futureTime + 1440 - startTime; return diffMinutes; } else { if (futureHours == 12) diffMinutes = futureTime - startTime; else diffMinutes = futureTime + 720 - startTime; return diffMinutes; } }
